mapper.insert:
INSERT INTO account_info
(ai_id, ai_pwd, ai_name, ai_phone, ai_birth)
VALUES(#{ai_id}, #{ai_pwd}, #{ai_name}, #{ai_phone}, #{ai_birth});

mapper.select:
SELECT ai_seq, ai_id, ai_pwd, ai_name, ai_phone, ai_birth, ai_reg_dt, ai_status, ai_grade
FROM account_info
where ai_id = #{ai_id} and ai_pwd = #{ai_pwd}
